-- Create tiktok_groups table create table tiktok_groups ( id uuid default gen_random_uuid() primary key, name text not null unique, created_at timestamp with time zone default timezone('utc'::text, now()) not null ); -- Create tiktok_group_members table create table tiktok_group_members ( group_id uuid references tiktok_groups(id) on delete cascade, username text references tiktok_users(username) on delete cascade, added_at timestamp with time zone default timezone('utc'::text, now()) not null, primary key (group_id, username) );
